con:config.script:
def before = testRunner.testCase.getPropertyValue("counter_init");
def after  = testRunner.testCase.getPropertyValue("counter_after");  
return after > before;

def quote_pickup  = testRunner.testCase.getPropertyValue("quote_pickup");
def quote_eta     = testRunner.testCase.getPropertyValue("quote_eta");
def parcel_pickup = testRunner.testCase.getPropertyValue("parcel_pickup");
def parcel_eta    = testRunner.testCase.getPropertyValue("parcel_eta");

if (quote_pickup != parcel_pickup)
{
	assert quote_eta != parcel_eta;
}
assert quote_pickup < quote_eta;
assert parcel_pickup < parcel_eta;

def before = testRunner.testCase.getPropertyValue("counter_init");
def after  = testRunner.testCase.getPropertyValue("counter_after");  
return after > before;

con:configuration.scriptText:
import com.eviware.soapui.support.XmlHolder def holder = new XmlHolder( messageExchange.responseContentAsXml )
def eta = holder["//eta"] as long def pickup = holder["//pickup"] as long
assert eta > pickup

con:config.script:
def before = testRunner.testCase.getPropertyValue("state_init");
def after  = testRunner.testCase.getPropertyValue("state_after");

if (before == "AWAITING_FOR_PICKUP")
{
	assert after == "PICKED UP";
}
else if (before == "IN_TRANSIT")
{
	assert after == "DELIVERED";
}

def before = testRunner.testCase.getPropertyValue("address_init");
def after  = testRunner.testCase.getPropertyValue("address_after");  
return after != before;
